/**
 * @jest-environment jsdom
 */
import React from 'react';
import { render, screen } from '@testing-library/react';
import { UserNav } from './user-nav';
import { useUser } from '@/firebase';

// Mock the useUser hook from Firebase
jest.mock('@/firebase', () => ({
  ...jest.requireActual('@/firebase'),
  useUser: jest.fn(),
}));

// Mock the useSidebar hook
jest.mock('./ui/sidebar', () => ({
  useSidebar: () => ({ open: true }),
}));

describe('UserNav Component', () => {

  it('shows a loading skeleton when user state is loading', () => {
    (useUser as jest.Mock).mockReturnValue({ user: null, isUserLoading: true });
    render(<UserNav />);
    // Skeletons don't have specific text, so we test for their presence by role or test-id if available
    // Assuming Skeleton component renders a div with role 'status' or similar
    expect(screen.getByRole('alert', { name: /loading/i })).toBeInTheDocument();
  });

  it('shows a login button when no user is authenticated', () => {
    (useUser as jest.Mock).mockReturnValue({ user: null, isUserLoading: false });
    render(<UserNav />);
    expect(screen.getByRole('link', { name: /login/i })).toBeInTheDocument();
  });

  it('displays user information when a user is authenticated', () => {
    const mockUser = {
      displayName: 'Jane Doe',
      email: 'jane.doe@example.com',
      photoURL: 'http://example.com/avatar.png',
    };
    (useUser as jest.Mock).mockReturnValue({ user: mockUser, isUserLoading: false });
    render(<UserNav />);
    expect(screen.getByText('Jane Doe')).toBeInTheDocument();
    expect(screen.getByText('jane.doe@example.com')).toBeInTheDocument();
    const avatar = screen.getByRole('img');
    expect(avatar).toHaveAttribute('src', mockUser.photoURL);
  });

  it('displays fallback initials if no photoURL is available', () => {
    const mockUser = {
      displayName: 'John Doe',
      email: 'john.doe@example.com',
      photoURL: null, // No photo
    };
    (useUser as jest.Mock).mockReturnValue({ user: mockUser, isUserLoading: false });
    render(<UserNav />);
    // The fallback avatar will contain the initials 'JD'
    expect(screen.getByText('JD')).toBeInTheDocument();
  });

});
